        $(document).ready(function () {
            GetData();

            $('#btnSave').click(function () {
                if (IsValidate()) {
                    var model = {
                        ""MaNV"": $('#MaNV').val(),
                        ""HoNV"": $('#HoNV').val(),
                        ""TenNV"": $('#TenNV').val(),
                        ""GioiTinh"": $('#GioiTinh').val() == ""Nữ"" ? true : false,
                        ""DiaChi"": $('#DiaChi').val(),
                        ""NgaySinh"": $('#NgaySinh').val(),
                        ""DienThoai"": $('#DienThoai').val()
                    };
                    $.ajax({
                        url: '/QuanLy/NhanViens/Create',
                        dataType: 'json',
                        type: 'POST',
                        data: model,
                        success: function (res) {
                            if (res.success) {
                                $('#modalCreate').modal('");
                WriteLiteral(@"hide');
                                GetData();
                                //window.location.reload();
                            }
                        },
                        error: function (xhr) { alert('error'); }
                    });
                }
            });
            //Edit
            $('body').on('click', '.btnEdit', function () {
                var id = $(this).data('id');
                console.log(""edit clicked "" + id);
                GetDataById(id);
            });

            //Save info from modal edit/update
            $('#btnEditSave').click(function () {
                var model = {
                    ""MaNV"": $('#editMaNV').val(),
                    ""HoNV"": $('#editHoNV').val(),
                    ""TenNV"": $('#editTenNV').val(),
                    ""GioiTinh"": $('#editGioiTinh').val() == ""Nữ"" ? false : true,
